<template>
  <select
    id="v-datatable-light-items-per-page"
    :class="css.select"
    @change="onUpdate"
  >
    <option
      v-for="item in listItemsPerPage"
      :key="item"
      :value="item"
      :selected="itemsPerPage === item"
    >
      {{ item }}
    </option>
  </select>
</template>
<style>
  #v-datatable-light-items-per-page {
    background-color: transparent;
    min-height: 30px;
    border: 1px solid #337ab7;
    border-radius: 5px;
    color: #337ab7;
  }
  #v-datatable-light-items-per-page:hover {
    cursor: pointer;
  }
</style>
<script>
export default {
	name: 'DataTableItemsDropdown',
	props: {
		listItemsPerPage: {
			type: Array,
			default: () => [10, 30, 50]
		},
		itemsPerPage: {
			type: Number,
			default: 10
    },
    css: {
      type: Object,
      default: () => ({
        select: ''
      })
    }
	},
	methods: {
		onUpdate: function (event) {
			this.$emit('onUpdate', parseInt(event.target.value, 10))
		}
	}

}
</script>
